void zero_threshold(int *data, size_t len) {
    for (size_t i = 0; i < len; i++) {
        int x = data[i];
        if (x < 0) data[i] = 0;
    }
}

void zero_threshold_expect(int *data, size_t len) {
    for (size_t i = 0; i < len; i++) {
        int x = data[i];
        if (__builtin_expect(x < 0, 0)) data[i] = 0;
    }
}
