template <size_t len> struct StringLiteral {
    constexpr StringLiteral(const char (&str)[len]) {
        std::copy_n(str, len, s);
    }
    constexpr operator std::string_view() const {
        return std::string_view(s, len - 1);
    }
    char s[len];
};

