struct UGLY {
    //roughly what C++ does
    enum v1 = 3;
    enum v2 = 4;
}
struct notUgly {
    uint v1;
    uint v2;
}
auto withUgly(T)()
{
    pragma(msg, "UGLY says ", T.v1 * T.v2);
}
auto withNotUgly(notUgly value)()
{
    pragma(msg, value.v1 * value.v2);
}

alias forceIns = withUgly!(UGLY);
alias _ = withNotUgly!(notUgly(5, 6));
