struct a{
    int i;
};

struct b{
    a* aptr;
};


int cmov1(int cond, a* v1, a* v2){
    return cond ? v1->i : v2->i;
}

// Add some extra indirection
int cmov2(int cond, a* v1, b* v2){
    return cond ? v1->i : v2->aptr->i;
}

// Load both values into variables as a hint to the compiler
int cmov3(int cond, a* v1, b* v2){
    int i1 = v1->i, i2 = v2->aptr->i;
    return cond ? i1: i2;
}

