void example_2_6_2_1() {
    std::string_view s = "foo"s;		// A
    s[0];   			// ERROR (lifetime.3): ‘s’ was invalidated when
						// temporary ‘"foo"s’ was destroyed (line A)
}

using namespace std;

extern bool cond;

void example_2_4_9_3() {
    int a[10], b[10];
    int i = 0;
    int* p = &a[0];	    			// pset(p) = {a}
    for( ; cond ; ) {
        *p = 42;
        p = nullptr;				// A: pset(p) = {null}
        // ...
        if(cond) {
            // ...
            p = &b[i];  			// pset(p) = {b}
            // ...
        }
        // merge => pset(p) = {null,b} for second iteration
        // ...
    }
}

