use std::mem;

fn always_returns_true(x: u8) -> bool {
    x < 150 || x > 120
}

pub fn main() {
    let x: u8 = unsafe { mem::uninitialized() };
    assert!(always_returns_true(x));
}

int blazeit(int x)
{
    return x == 7 ? 420 : blazeit(x++);
}
